ALTER PROCEDURE SP_A_GET_MINMAX
RETURNS (
    RDLMINYR INTEGER,
    RDLMINMT INTEGER,
    RDLMAXYR INTEGER,
    RDLMAXMT INTEGER,
    RAMINYR INTEGER,
    RAMINMT INTEGER,
    RAMAXYR INTEGER,
    RAMAXMT INTEGER)
AS
declare k1 integer;
begin

    RDLMINYR = 0;
    RDLMINMT = 0;
    RDLMAXYR = 0;
    RDLMAXMT = 0;
    RAMINYR = 0;
    RAMINMT = 0;
    RAMAXYR = 0;
    RAMAXMT = 0;

    select min(YR * 13 + MT)
    from a_salary_sheets
    into :k1;

    if (k1 is null) then
        k1 = 0;
    else
        select min(yr), min(mt)
        from a_salary_sheets
        where (yr * 13 + mt) = :k1
        into :RDLMINYR, RDLMINMT;

    select max(YR * 13 + MT)
    from a_salary_sheets
    into :k1;

    if (k1 is null) then
        k1 = 0;
    else
        select max(yr), max(mt)
        from a_salary_sheets
        where (yr * 13 + mt) = :k1
        into :RDLMAXYR, RDLMAXMT;

    select min(YR * 13 + MT)
    from a_timesheet_lists
    into :k1;

    if (k1 is null) then
        k1 = 0;
    else
        select min(yr), min(mt)
        from a_timesheet_lists
        where (yr * 13 + mt) = :k1
        into :RAMINYR, RAMINMT;


    select max(YR * 13 + MT)
    from a_timesheet_lists
    into :k1;

    if (k1 is null) then
        k1 = 0;
    else
        select max(yr), max(mt)
        from a_timesheet_lists
        where (yr * 13 + mt) = :k1
        into :RAMAXYR, RAMAXMT;


  suspend;
end^


