            join a_paylists_r
            on  (
                    a_paylists_r.ids = :pids and
                    a_paylists_r.idam = a_salary_sheets_r.idam
                )
            join
            (
                select
                    ssr2.idam as xidam,
                    max(ss2.dt2) as xdt
                from
                    a_salary_sheets as ss2
                    join a_salary_sheets_r as ssr2
                    on  (
