        from a_salary_sheets
        join a_salary_sheets_r on a_salary_sheets.id = a_salary_sheets_r.ids
    
        where
            (a_salary_sheets.dt2 between :pdt1 and :pdt2) AND
            (a_salary_sheets.is_temp = 0) AND
