ALTER PROCEDURE SP_A_REP_IINK_P1 (
    PDT1 DATE,
    PDT2 DATE,
    PDTY DATE)
RETURNS (
    IDP INTEGER,
    HDATE DATE,
    FDATE DATE)
AS
begin
/* darbinieki, kas atlaisti norƒÅdƒ´tajƒÅ periodƒÅ */

    for select
        FT.idp,
        maxvalue(max(EV.date1), :pdty) as hdate,
        FT.fdate
    from
        a_events as EV
        join 
        (
            select distinct EV3.idp, FT2.xdate as fdate
            from a_events as EV3
            join
            (
                select
                    EV2.idp,
                    max(EV2.date1) as xdate
                from a_events as EV2
                where
                    EV2.idn in (1, 2) and
                    EV2.date1 between :pdt1 and :pdt2
                group by EV2.idp
            ) as FT2
            on EV3.idp = FT2.idp and EV3.date1 = FT2.xdate
            where EV3.idn = 2
        ) as FT
        on (FT.idp = EV.idp)

    where
        EV.idn = 1 and
        EV.date1 < FT.fdate
    group by FT.idp, FT.fdate

    into IDP, HDATE, FDATE

    do
    begin
        suspend;
    end
end^


