            on a_temp_rep_inkp.idp = a_salary_sheet_templ_r.idam
        where a_salary_sheet_templ_r.id is null
    
        union all
    
    
        select a_salary_sheets_r.idp as idp, a_temp_rep_inkp.idp as ida, 9 as tag
        from a_temp_rep_inkp
        left join
        (
            a_salary_sheets_r
