ALTER PROCEDURE SP_F_DIFF_11 (
    PDATE1 DATE,
    PDATE2 DATE,
    PDATE3 DATE)
RETURNS (
    IDL1 TYPE OF COLUMN F_LOPSD.IDL,
    IDL2 TYPE OF COLUMN F_LOPS.IDL,
    DOCID TYPE OF COLUMN F_LOPSD.ID,
    ROWID TYPE OF COLUMN F_LOPS.ID)
AS
begin
    for select distinct
        idl1, idl2, docid, rowid
    from
    (
        select
            T3.idl as idl1,
            T4.idl as idl2,
            T3.id as docid,
            T4.id as rowid
        from
        (
            select T1.idl, T1.id
            from f_lopsd as T1
            where
                T1.dete between :pdate1 and :pdate2 and
                T1.odt < :pdate3 and
                not exists (
                    select idl
                    from f_lopsd as T2
                    where
                        T2.id = T1.id and
                        T2.odt < :pdate3 and
                        (T2.odt > T1.odt or
                        T2.odt = T1.odt and
                        T2.idl > T1.idl)
                )
        ) as T3
        join f_lops as T4
        on T3.id = T4.docid and
            T4.odt < :pdate3 and
            not exists (
                select idl
                from f_lops as T5
                where
                    T5.id = T4.id and
                    T5.odt < :pdate3 and
                    (T5.odt > T4.odt or
                    T5.odt = T4.odt and
                    T5.idl > T4.idl)
                    
            )

        union all

        select
            T4.idl as idl1,
            null as idl2,
            T4.id as docid,
            T5.id as rowid
        from
        (
            select T1.idl, T1.id
            from f_lopsd as T1
            where
                T1.dete between :pdate1 and :pdate2 and
                T1.odt < :pdate3 and
                not exists (
                    select idl
                    from f_lopsd as T2
                    where
                        T2.id = T1.id and
                        T2.odt < :pdate3 and
                        (T2.odt > T1.odt or
                        T2.odt = T1.odt and
                        T2.idl > T1.idl)
                )
                and not exists (
                    select idl
                    from f_lops as T3
                    where T3.docid = T1.id
                )
        ) as T4
        join f_ops as T5
        on T5.id = T4.id and
            T5.zdt < :pdate3
    )
    into
        :idl1, :idl2, :docid, :rowid
    do
    suspend;
end^


