ALTER PROCEDURE SP_F_LINKEDDOCS_1 (
    PSD DATE,
    PED DATE,
    PCLID VARCHAR(15),
    PDOCNR VARCHAR(15))
RETURNS (
    ID INTEGER,
    DETE DATE,
    CLID VARCHAR(15),
    DOCTYP VARCHAR(10),
    DOCST VARCHAR(6),
    DOCNR VARCHAR(15),
    DESCR VARCHAR(80),
    SUMM NUMERIC(15,4),
    PVN NUMERIC(15,4))
AS
declare variable k integer;
begin
    k = GEN_ID("GEN_F_OPSD_ID",0) + 1;

    FOR SELECT
        ID,
        DETE,
        clid,
        (DocTyp),
        DocSt,
        DocNr,
        Descr,
        Summ,
        PVN
    FROM
    (
        SELECT
            NULL AS ID,
            DETE,
            F_DOCS0.clid,
            F_DOCS0.DocTyp,
            F_DOCS0.DocSt,
            F_DOCS0.DocNr,
            F_DOCS0.Descr,
            CASE WHEN F_DOCS0.ac STARTING WITH '2'
                THEN summ
                ELSE -summ
            END AS Summ,
            CASE WHEN F_DOCS0.ac STARTING WITH '2'
                THEN pvn
                ELSE -pvn
            END AS PVN
        FROM F_DOCS0
        WHERE
            F_DOCS0.ClId = :PCLID AND
            (:PDOCNR is null OR F_DOCS0.DocNr = :PDOCNR)

        UNION ALL
    
        SELECT
            f_opsd.id,
            f_opsd.DETE,
            f_opsd.clid,
            f_opsd.DocTyp,
            f_opsd.DocSt,
            f_opsd.DocNr,
            f_opsd.Descr,
            CASE WHEN AP1 = 1 OR AP2 = 1
                THEN -F_OPSd.Summ
                ELSE F_OPSd.Summ
            END AS Summ,
            CASE WHEN AP1 = 1 OR AP2 = 1
                THEN -F_OPSd.PVN
                ELSE F_OPSd.PVN
            END AS PVN
        FROM
        (
            SELECT 
                F_OPSd.id, 
                MAX(CASE WHEN AcP21_1.AC IS NULL THEN 0 ELSE 1 END) AS AP1,
                MAX(CASE WHEN AcP21_2.AC IS NULL THEN 0 ELSE 1 END) AS AP2
            FROM
                F_OPS INNER JOIN F_OPSd ON F_OPSd.id = F_OPS.DocId
                LEFT JOIN F_AcP21 AS AcP21_1 ON F_OPS.AC11 = AcP21_1.AC AND AcP21_1.id1 IN ('KA','BA','CN')
                LEFT JOIN F_AcP21 AS AcP21_2 ON F_OPS.AC21 = AcP21_2.AC AND AcP21_2.id1 IN ('KA','BA','CN')
            WHERE
                F_OPSd.DETE BETWEEN :PSD AND :PED AND
                f_opsd.ClId = :PCLID AND
                (:PDOCNR is null OR f_opsd.DocNr = :PDOCNR)
            GROUP BY F_OPSd.id
        ) AS T1 
        INNER JOIN F_OPSd ON T1.id = F_OPSd.id

    )
    order by DETE

    INTO :ID, :DETE, :clid, :doctyp, :docst, :docnr, :descr, :summ, :pvn
    do
    begin
        if (ID is null) then
        begin
            ID = k;
            k = k + 1;
        end
        suspend;
    end
end^


