ALTER PROCEDURE SP_F_REP_BAL_11 (
    SDT DATE,
    EDT DATE)
RETURNS (
    AC VARCHAR(8),
    S1 MONEY,
    S2 MONEY)
AS
begin
    FOR SELECT
        T2.AC AS AC,
        T2.ADb - T2.ACr AS S1,
        T2.BDb - T2.BCr AS S2
    FROM
    (
        SELECT AC, SUM(SDb) AS ADb, SUM(SCr) AS ACr, SUM(TDb) AS BDb, SUM(TCr) AS BCr
        FROM
        (
            SELECT 
                F_OPS.AC11 AS AC,
                CASE
                    WHEN F_OPSd.Dete < :sdt THEN F_OPS.Summ
                    ELSE 0.00
                END AS SDb,
                0.00 AS SCr,
                CASE
                    WHEN F_OPSd.Dete >= :sdt THEN F_OPS.Summ
                    ELSE 0.00
                END AS TDb,
                0.00 AS TCr

            FROM F_OPSd JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
            WHERE F_OPSd.Dete <= :edt

            UNION ALL

            SELECT 
                F_OPS.AC21 AS AC,
                0.00 AS SDb,
                CASE
                    WHEN F_OPSd.Dete < :sdt THEN F_OPS.Summ
                    ELSE 0.00
                END AS SCr,
                0.00 AS TDb,
                CASE
                    WHEN F_OPSd.Dete >= :sdt THEN F_OPS.Summ
                    ELSE 0.00
                END AS TCr

            FROM F_OPSd JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
            WHERE F_OPSd.Dete <= :edt

            UNION ALL

            SELECT 
                F_Bal0.AC11 AS AC,
                F_Bal0.SummD AS SDb,
                F_Bal0.SummC AS SCr,
                0.00 AS TDb,
                0.00 AS TCr
            FROM F_Bal0

        ) AS T1

        GROUP BY T1.AC
    ) AS T2    

    WHERE (ADb - ACr) <> 0.00 OR BDb <> 0.00 OR BCr <> 0.00

    ORDER BY AC

    INTO
        :AC, :S1, S2
    DO
    BEGIN
        SUSPEND;
    END
end^


