        T2.BCr AS TCr,
        T2.Clid AS Clid,
        F_Persons.Name AS Name1,
        F_Persons.regnr AS regnr,
        null as rname
    FROM
    (
        SELECT AC, T1.Clid AS Clid, SUM(SDb) AS ADb, SUM(SCr) AS ACr, SUM(TDb) AS BDb, SUM(TCr) AS BCr
        FROM
        (
            -- :pcur ***************
            SELECT 
                F_OPS.AC11 AS AC,
                F_OPSd.ClId AS Clid,
                F_OPS.SummC AS SDb,
                0.00 AS SCr,
                F_OPS.Summ AS TDb,
                0.00 AS TCr

            FROM F_OPSd JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
            WHERE 
                F_OPSd.Dete <= :edt AND 
                F_OPS.AC11 = :pac AND 
                F_OPS.Cur = :pcur AND
                (:pclid IS NULL OR F_OPSd.ClId = :pclid)

            UNION ALL

            SELECT 
                F_OPS.AC21 AS AC,
                F_OPSd.ClId AS Clid,
                0.00 AS SDb,
                F_OPS.SummC AS SCr,
                0.00 AS TDb,
                F_OPS.Summ AS TCr


            FROM F_OPSd JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
            WHERE 
                F_OPSd.Dete <= :edt AND 
                F_OPS.AC21 = :pac AND 
                F_OPS.Cur = :pcur AND
                (:pclid IS NULL OR F_OPSd.ClId = :pclid)

            -- EUR ***************
            UNION ALL

            SELECT 
                F_OPS.AC11 AS AC,
                F_OPSd.ClId AS Clid,
                0.00 AS SDb,
                0.00 AS SCr,
                F_OPS.Summ AS TDb,
                0.00 AS TCr

            FROM F_OPSd JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
            WHERE 
                F_OPSd.Dete <= :edt AND 
                F_OPS.AC11 = :pac AND 
                F_OPS.Cur = 'EUR' AND
                (:pclid IS NULL OR F_OPSd.ClId = :pclid)

            UNION ALL

            SELECT 
                F_OPS.AC21 AS AC,
                F_OPSd.ClId AS Clid,
                0.00 AS SDb,
                0.00 AS SCr,
                0.00 AS TDb,
                F_OPS.Summ AS TCr


            FROM F_OPSd JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
            WHERE 
                F_OPSd.Dete <= :edt AND 
                F_OPS.AC21 = :pac AND 
                F_OPS.Cur = 'EUR' AND
                (:pclid IS NULL OR F_OPSd.ClId = :pclid)

            UNION ALL

            SELECT 
                F_Bal0.AC11 AS AC,
                F_Bal0.ClId AS Clid,
                F_Bal0.SummDC AS SDb,
                F_Bal0.SummCC AS SCr,
                F_Bal0.SummD AS TDb,
                F_Bal0.SummC AS TCr
            FROM F_Bal0
            WHERE 
                F_Bal0.AC11 = :pac AND 
                F_Bal0.Cur = :pcur AND
                (:pclid IS NULL OR F_Bal0.ClId = :pclid)
            
            UNION ALL

            SELECT 
                F_Bal0.AC11 AS AC,
                F_Bal0.ClId AS Clid,
                0.00 AS SDb,
                0.00 AS SCr,
                F_Bal0.SummD AS TDb,
                F_Bal0.SummC AS TCr
            FROM F_Bal0
            WHERE 
                F_Bal0.AC11 = :pac AND 
                F_Bal0.Cur = 'EUR' AND
                (:pclid IS NULL OR F_Bal0.ClId = :pclid)

        ) AS T1
        GROUP BY AC, T1.Clid

    ) AS T2    
    LEFT JOIN F_AcP21 ON T2.AC = F_AcP21.AC
    LEFT JOIN F_Persons ON T2.Clid = F_Persons.ClId
    ORDER BY AC

    
    into
        :id, :spid, :ac, :rac, :name,
        :b0,
        :sdb, :scr, :tdb, :tcr,
        :clid, :name1, :regnr, :rname
    do
    begin
        suspend;
    end
end^


