AS
begin
-- PVN 1.3. pielikums     (ie≈Üƒìmumi)
    FOR SELECT
        did,
        T2.Clid,
        CASE
            WHEN Summ > :rg1 AND
                (F_DocTyp.pvnpaz IS NULL OR
                    T2.ClId IS NULL OR
                    F_Persons.TP3 = 'Nav') THEN 'X'
            ELSE F_DocTyp.pvnpaz
        END AS DocTyp1,
        DocTyp2, 
        DocTypC, 
        RAC5,
        Summ, 
        PVN,
        0.0 AS summc,
        null AS cur
    FROM
    (
        SELECT 
            T1.did,
            MAX(T1.ClId) AS ClId,
            MAX(T1.DocTyp1) AS DocTyp1,
            MAX(T1.DocTyp2) AS DocTyp2,
            MAX(T1.DocTypC) AS DocTypC,
            MAX(T1.RAC5) AS RAC5,
            SUM(T1.SummC - T1.SummD) AS Summ,
            SUM(T1.PVNC - T1.PVND) AS PVN
        FROM
        (
            SELECT 
                F_OPSd.id AS did, 
                F_OPSd.ClId AS ClId,
                F_OPSd.DocTyp AS DocTyp1,
                F_AcPVN.pz1 AS DocTyp2,
                F_AcPVN.pz4 AS DocTypC,
                F_OPS.AC25 AS RAC5,
                0.00 AS SummD,
                0.00 AS PVND,
                0.00 AS SummC,
                F_OPS.Summ AS PVNC
            FROM
                F_OPSd INNER JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
                INNER JOIN F_AcPVN ON F_OPS.AC25 = F_AcPVN.id
            WHERE
                (F_OPSd.Dete BETWEEN :psd AND :ped) AND
                F_AcPVN.pz3 IS NOT NULL AND 
                F_AcPVN.pz3 IN ('2') 

            UNION ALL

            SELECT 
                F_OPSd.id AS did, 
                F_OPSd.ClId AS ClId,
                F_OPSd.DocTyp AS DocTyp1,
                F_AcPVN.pz1 AS DocTyp2,
                F_AcPVN.pz4 AS DocTypC,
                F_OPS.AC25 AS RAC5,
                0.00 AS SummD,
                0.00 AS PVND,
                F_OPS.Summ AS SummC,
                0.00 AS PVNC
            FROM
                F_OPSd INNER JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
                LEFT JOIN F_AcPVN ON F_OPS.AC25 = F_AcPVN.id
            WHERE
                F_OPSd.Dete BETWEEN :psd AND :ped AND
                F_AcPVN.pz3 IS NOT NULL AND 
                F_AcPVN.pz3 IN ('1') 

            UNION ALL

            --ATGRIEZTA PRECE
            SELECT 
                F_OPSd.id AS did, 
                F_OPSd.ClId AS ClId,
                F_OPSd.DocTyp AS DocTyp1,
                F_AcPVN.pz1 AS DocTyp2,
                F_AcPVN.pz4 AS DocTypC,
                F_OPS.AC15 AS RAC5,
                0.00 AS SummD,
                F_OPS.Summ AS PVND,
                0.00 AS SummC,
                0.00 AS PVNC
            FROM
                F_OPSd INNER JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
                INNER JOIN F_AcPVN ON F_OPS.AC15 = F_AcPVN.id
            WHERE
                (F_OPSd.Dete BETWEEN :psd AND :ped) AND
                F_AcPVN.pz3 IS NOT NULL AND 
                F_AcPVN.pz3 IN ('2') AND
                F_AcPVN.pz1 IS NOT NULL AND
                F_AcPVN.pz1 NOT IN ('41', '42')

            UNION ALL

            SELECT 
                F_OPSd.id AS did, 
                F_OPSd.ClId AS ClId,
                F_OPSd.DocTyp AS DocTyp1,
                F_AcPVN.pz1 AS DocTyp2,
                F_AcPVN.pz4 AS DocTypC,
                F_OPS.AC15 AS RAC5,
                F_OPS.Summ AS SummD,
                0.00 AS PVND,
                0.00 AS SummC,
                0.00 AS PVNC
            FROM
                F_OPSd INNER JOIN F_OPS ON F_OPSd.id = F_OPS.DocId
                LEFT JOIN F_AcPVN ON F_OPS.AC15 = F_AcPVN.id
            WHERE
                F_OPSd.Dete BETWEEN :psd AND :ped AND
                F_AcPVN.pz3 IS NOT NULL AND 
                F_AcPVN.pz3 IN ('1') AND
                F_AcPVN.pz1 IS NOT NULL AND
                F_AcPVN.pz1 NOT IN ('41', '42')



        ) AS T1

        GROUP BY T1.did

    ) AS T2
        LEFT JOIN F_Persons ON T2.ClId = F_Persons.clid
        LEFT JOIN F_DocTyp ON T2.DocTyp1 = F_DocTyp.id

    WHERE 
        T2.Summ <> 0.00 AND
        T2.DocTyp2 IS NOT NULL

    into
        :did, :clid, :doctyp1, :doctyp2, :doctypc,
        :rac5, :summ, :pvn, :summc, :cur
    do
    begin
        suspend;
    end


end^


