        EXECUTE STATEMENT s;
        i = i + 1;
    end
end^


ALTER PROCEDURE SP_F_SET_KIEONR (
    SDT DATE,
    EDT DATE,
    PAC VARCHAR(8),
    PNR INTEGER)
AS
declare variable ID INT;
    declare variable Nr2 INT;
begin
    Nr2 = pnr;
    FOR SELECT
        id
    FROM F_OPSd
    WHERE
        EXISTS
        (
            SELECT NULL
            FROM F_OPS
            WHERE 
                F_OPSd.id = F_OPS.DocId AND
                F_OPS.AC11 = :pac
        ) AND
        F_OPSd.Dete BETWEEN :sdt AND :edt

    ORDER BY Dete, id

    INTO :ID
    DO
    BEGIN

        UPDATE F_OPSd
        SET NrX = :Nr2
        WHERE F_OPSd.id = :ID;

        nr2 = nr2 + 1;
    END
end^


