    from m_rows join m_docs on m_docs.id = m_rows.iddoc
    where m_rows.id = :vidcredrow
    into :vbuyprice, :vdt2;

    vtbuyprice = round(vamounttolink * vbuyprice, 2);

    update m_rows
    set
        buyprice = :vbuyprice,
        tbuyprice = :vtbuyprice
    where id = :pidrow and
        (buyprice <> :vbuyprice or
        tbuyprice <> :vtbuyprice);

    suspend;
end^


