        join m_storetype as ST2 on ST2.id = S2.tp and
            ST2.trackstock = 1
        join m_items on m_items.id = R1.iditem and
            m_items.isservice = 0
        group by D1.tp, R1.acc7, D1.accout
    ) as T1
    order by tp
    into :tp, :acc1, :acc2, :cost
