            join m_docs as D1 on R1.iditem = :piditem and
                D1.id = R1.iddoc and
                D1.state in (2,3)
            join m_doctypes as DT1 on DT1.id = D1.tp and
                DT1.optype = 1
            left join m_links as L1 on L1.id1 = R1.id
        ) as left_part
    
        left join
    
        (
            select
                D1.id as idd,
                R1.id as idr,
                D1.idseq,
                D1.dt,
                D1.sr,
                D1.nr,
                D1.tp,
                D1.idstorein,
                D1.idstoreout,
                R1.amount,
                R1.buyprice
            from m_rows as R1
            join m_docs as D1 on R1.iditem = :piditem and
                D1.id = R1.iddoc and
                D1.state in (2,3)
            join m_doctypes as DT1 on DT1.id = D1.tp and
                DT1.optype = 2
        ) as right_part on left_part.id2 = right_part.idr
        where (left_part.dt between :pdt1 and :pdt2 or
            right_part.dt between :pdt1 and :pdt2)
    
        union all

        select
            null as aidd,
            null as aidr,
            null as aidseq,
            null as adt,
            null as asr,
            null as anr,
            null as atp,
            null as aidstorein,
            null as aidstoreout,
            null as aamount,
