            R1.id as bidr,
            D1.idseq as bidseq,
            D1.dt as bdt,
            D1.sr as bsr,
            D1.nr as bnr,
            D1.tp as btp,
            D1.idstorein as bidstorein,
            D1.idstoreout as bidstoreout,
            R1.amount as bamount,
            R1.buyprice as bbuyprice,

            null as amountlinked

        from m_rows as R1
        join m_docs as D1 on R1.iditem = :piditem and
            D1.id = R1.iddoc and
